hijackWithValidationErrors.script:
function (ctx, request) {
  var messages = request.validate();

  // did the request validation fail?
  if (!request.valid.get()) {
    // respond immediately with the validation error messages
    // this request will never hit the end api server
    request.hijack(400, "application/json", messages)
  }
}

queryStringMocking.script:
function (ctx, request) {
  // For example, adding ?mock=200 to a request url will enable mocking,
  // using the example endpoint response for the 200 status code.
  var mock = request.url.query.get("mock")
  if (mock) {
    ctx.mock.set(true, mock)
  }
}

mapPath.script:
function (ctx, request, originSubpath, targetSubpath) {
  // for example, mapPath(ctx, request, "users", "members") 
  // will rewrite an incoming request from /users/123 to /members/123.
  var path = request.url.path.get();
  if (path.indexOf(originSubpath) !== -1) {
     path.replace(originSubpath, targetSubpath);
     request.url.path.set(path);
  }
}

